        loyaltyProductList.add(new LoyaltyProduct("Kawa lawazza", "https://encrypted-tbn3.gstatic.com/shopping?q=tbn:ANd9GcQGGWS4zRR7U9N6Z0CpMJ3ZHeWhBv9nRmbBudsIX419yhkJIpDEJsdr1TGOmQPKBEJbx0Gvm0fC_FY&usqp=CAc", "other", 200));
        loyaltyProductList.add(new LoyaltyProduct("Kotlety mięsne wołowe", "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcS_tNrWoTaS8zmmgUwuNqZWaPSFCLe4b1NbF2GpqKL29A7ZTVNEKrkZtbJia-Ia80thboTIfzc_xApVHWG1UMevsSve4BIA2uQJuOI3EFAu0Rayb9xrnRgcNA&usqp=CAE" , "meat", 120));
        loyaltyProductList.add(new LoyaltyProduct("Herbata lipton", "https://encrypted-tbn0.gstatic.com/shopping?q=tbn:ANd9GcRzQJM2NP2cH-MMCHgRH-IeBdRCBLs_buR06E6cGlft3vDHQ9evOIERPJXpuDQ-xL-4gapHztchmfE&usqp=CAc", "other", 40));
        loyaltyProductList.add(new LoyaltyProduct("Fasola 1kg", "https://encrypted-tbn2.gstatic.com/shopping?q=tbn:ANd9GcTdq31KyVAB9ogGKGNAKMz88S72vwJWFCKhyObBdYxyf6LB1LDhvdo2zgTIv4yMPhdnGvNuU7KiteHemdYI2vvTPE8S2HnN9-L4YsoHUYVjgxQyw1ROlHGdow&usqp=CAE", "vegetable", 60));
        loyaltyProductList.add(new LoyaltyProduct("Cebula 1kg", "data:image/png;base64,", "vegetable", 50));
