        loyaltyProductList.add(new LoyaltyProduct("Koszyk truskawek", "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSru-IWBIflqBoplz477vat03tw8f5N3BRqZNIKP2IQA&s", "fruit", 80));
